
// Other Node Modules
var del = require("del");

/**
 * Removes the files generated by the gulp config task..
 */
module.exports = function(gulp, plugins) {

    return function(cb) {

        del([
            "config.xml",
            "www/js/build-vars.js"
        ]).then(function () {
            cb();
        });
    };
};
